#Create lists for each n-grams
list_4grams = []
list_3grams = []
list_2grams = []
#Choose the list to call
for element in war:
#for element in other:
#Each token will be split into unique or several units of the number of characters called as the second argument
    ngrams = wrap(element, 4)
    ngrams = str(ngrams)
    list_4grams.append(ngrams)
    ngrams = wrap(element, 3)
    ngrams = str(ngrams)
    list_3grams.append(ngrams)
    ngrams = wrap(element, 2)
