#Create lists for each n-grams
list_4grams = []
list_3grams = []
list_2grams = []
#Choose the list to call
for element in gt:
#for element in war:
#for element in other:
#Each token will be split into unique or several units of the number of characters called as the second argument
    ngrams = wrap(element, 4)
    ngrams = str(ngrams)
    list_4grams.append(ngrams)
    ngrams = wrap(element, 3)
    ngrams = str(ngrams)
    list_3grams.append(ngrams)
    ngrams = wrap(element, 2)
    ngrams = str(ngrams)
    list_2grams.append(ngrams)
fourgrams = "".join(list_4grams).replace("][", ", ")
trigrams = "".join(list_3grams).replace("][", ", ")
bigrams = "".join(list_2grams).replace("][", ", ")
with open(sys.argv[1], "w") as file_out:
    print(f'Writing to {sys.argv[1]}')
    file_out.write("fourgrams = " + fourgrams + "\n\n")
    file_out.write("trigrams = " + trigrams + "\n\n")
    file_out.write("bigrams = " + bigrams)